require(['lib/tools/jit'],

application.plasmoids['A2904713-0E33-40F7-BC4C-6F959DFE3AE8'] = function(jit) {

function create_graph(){    
  var infovis = $('#plasmoid_A2904713-0E33-40F7-BC4C-6F959DFE3AE8').get(0);
  var w = infovis.offsetWidth;
  var h = infovis.offsetHeight;

  var ht = new $jit.Hypertree({
  injectInto:'plasmoid_A2904713-0E33-40F7-BC4C-6F959DFE3AE8',
  width:w,
  height:h,
  Node: {dim:9,color:"#65a1cf"},
  Edge: {lineWidth:2, color:"#666"},
  duration: 800,
  Events: {
    enable: true, 
    type: 'Native',
    onClick: function(node, eventInfo, e) {
      if(node != false) {
      application.functions.hyki.route('/hyki/'+node.name+'/', true);
      ht.move(node.pos.getc(),{hideLabels:true});
      }
    }
  },
  Navigation: {
    enable: true,
    panning: 'avoid nodes',
    zooming: 20
  },
  onBeforeCompute: function(node) {

  },
  onCreateLabel: function(domElement, node){
    domElement.innerHTML = node.name;
  },
  onPlaceLabel: function(domElement , node){
    var style = domElement.style;
    style.display = '';
    style.cursor = 'default';
    if(node._depth <=1) {
    style.fontSize = "0.8em";
    style.color = "#333";
    }
    else if(node._depth == 2){
    style.fontSize = "0.7em";
    style.color = "#555";
    }
    else {
    style.display = 'none';
    }
  var left = parseInt(style.left);
  var w = domElement.offsetWidth;
  style.left = (left -w / 2) + 'px';
  },
  onAfterCompute: function() {
    var node = ht.graph.getClosestNodeToOrigin("current");
    var html = "<h4>" + node.name + "</h4><b>Connections:</b>";
    html += "<ul>";
    node.eachAdjacency(function(adj){
    var child = adj.nodeTo;
      if(child.data) {
      var rel = (child.data.band == node.name) ? child.data.relation : node.data.relation;
      html += "<li>" + child.name + " " + "<div class='relation'>(relation: " + rel + ")</div></li>";
      }
    });
    html += "</ul>";
  }
  });
  application.ws.remote('/data/hyki/tree/',{},function(response){
  ht.loadJSON(response.data.tree);
  ht.refresh();
  });

  $(document).bind('HYKI_ARTICLE_LINK_FOLLOWED', function(event, params){
  var node = ht.graph.getByName(params.slug);
    if(node) {
    ht.move(node.pos.getc(), {hideLabels: true});
    }
  });

  $(document).bind('HYKI_ARTICLE_CREATED HYKI_ARTICLE_SAVED HYKI_ARTICLE_DELETED', function(event, params) {
    application.ws.remote('/data/hyki/tree/',{}, function(response){
    ht.loadJSON(response.data.tree);
    ht.refresh();
    });
  });
}

create_graph();

});
